package com.study.dao;import java.util.List;import javax.annotation.Resource;import org.hibernate.SessionFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.orm.hibernate3.HibernateTemplate;import org.springframework.stereotype.Repository;import com.study.dao.base.AbstractBaseORMDao;import com.study.model.Dictionary;@Repository("dictionaryDao")public class DictionaryDaoImpl extends AbstractBaseORMDao<Dictionary, Long> implements IDictionaryDao {	private static final long serialVersionUID = 2725175497706339564L;	@Autowired	private HibernateTemplate hibernateTemplate;		@Resource(name = "sessionFactory")	public void setSuperSessionFactory(SessionFactory sessionFactory) {		super.setSessionFactory(sessionFactory);	}	@Override	public Dictionary getByWord(final String word) {		String sql = "From Dictionary where word = ?";		@SuppressWarnings("unchecked")		List<Dictionary> list = hibernateTemplate.find(sql, new String[]{word});		return list.size()>0 ? list.get(0) : null;	}}